{"changed":false,"filter":false,"title":"CusList.php","tooltip":"/queries/CusList.php","value":"<?php\n\ninclude 'config.php';\n\n$AllCustomer = \"SELECT * FROM CUSTOMER ORDER BY CUS_LNAME ASC\";\n$AllCusResult = mysqli_query($conn, $AllCustomer);\n\n//Error message if query doesn't work\nif ($AllCusResult == false ) {\n  printf(\"Query error: %s\\n\", mysqli_error($conn));\n}\n\n\n//For search query\nif ($_SERVER['REQUEST_METHOD'] == 'POST'){\n    $search = $_POST['cusSearch'];\n    \n    $cSearch = \"SELECT * FROM CUSTOMER WHERE (CUS_FNAME LIKE '%\" . $search . \"%') OR (CUS_LNAME LIKE '%\" . $search . \"%')\";\n    $cSResult = mysqli_query($conn, $cSearch);\n\n    if ($cSResult == false ) {\n        printf(\"Query error: %s\\n\", mysqli_error($conn));\n    }\n\n    $thisQuery = $cSResult;\n} else {\n    $thisQuery = $AllCusResult;\n}\n\nif ($dropdown != true){\n    $Count = 1;\n    \n    if (mysqli_num_rows($thisQuery) > 0) {\n        $row = mysqli_fetch_assoc($thisQuery);\n        while($row) {\n            echo '  <tr>\n                      <th scope=\"row\"><a href=\"CusSummary.php?id=' . $row[\"CUS_ID\"] . '\" style=\"text-decoration:none\">' . $Count . '</a></th>\n                      <td>' . $row[\"CUS_FNAME\"] . '</td>\n                      <td>' . $row[\"CUS_LNAME\"] . '</td>\n                      <td>' . $row[\"CUS_EMAIL\"] . '</td>\n                      <td>' . $row[\"CUS_PHONE\"] . '</td>\n                      <td>        \n                          <a href=\"EditCus.php?id=' . $row[\"CUS_ID\"] . '\" style=\"text-decoration:none\">\n                              <span class=\"glyphicon\">&#x270f;</span>\n                          </a>\n                      </td>\n                    </tr>';\n            $Count = $Count + 1;\n            $row = mysqli_fetch_assoc($thisQuery);\n        }\n    } else {\n        echo \"<tr> No customers to display.</tr>\";\n    }\n    \n//DROPDOWN LIST USED IN FORMS\n} else {\n    $row = mysqli_fetch_assoc($thisQuery);\n    while($row){\n        echo '<option value=\"'. $row[\"CUS_ID\"] .'\">' . $row[\"CUS_FNAME\"] . ' ' . $row[\"CUS_LNAME\"] . '</option>';\n        $row = mysqli_fetch_assoc($thisQuery);\n    }\n}\nmysqli_close($conn);\n\n?>","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":240,"scrollleft":0,"selection":{"start":{"row":36,"column":81},"end":{"row":36,"column":81},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":16,"state":"php-start","mode":"ace/mode/php"}},"timestamp":1554607350092}